--[[ 
  Функция для оформления идентификатора сущности

  Принимает: строку индентификатора (типа Q42) и таблицу параметров,
  Возвращает: строку оформленного текста
]]
function formatEntityId( entityId, options )
	-- получение локализованного названия 
    local label = nil;
    if ( options.text and options.text ~= '' ) then
        label = options.text
    else
    	if ( simpleReplaces[entityId] ) then
			return simpleReplaces[entityId];
		end
		label = mw.wikibase.label( entityId );
    end

	-- получение ссылки по идентификатору
    local link = mw.wikibase.sitelink( entityId )
    if link then
        if label then
            return '[[' .. link .. '|' .. label .. ']]'
        else
            return '[[' .. link .. ']]'
        end
    end

    if label then
        return label
    end
    -- сообщение об отсутвии локализованного названия
    -- not good, but better than nothing
    return '[[d:' .. entityId .. '|' .. entityId .. ']]<span style="border-bottom: 1px dotted; cursor: help; white-space: nowrap" title="В Викиданных нет русской подписи к элементу. Вы можете помочь, указав русский вариант подписи.">?</span>' .. categoryLinksToEntitiesWithMissingLabel;
end

--[[ 
  Функция для оформления утверждений (statement)
  Подробнее о утверждениях см. d:Wikidata:Glossary/ru

  Принимает: таблицу параметров
  Возвращает: строку оформленного текста, предназначенного для отображения в статье
]]
-- устаревшее имя, не использовать
function p.formatStatements( frame )
	return p.formatProperty( frame );
end

function p.formatProperty( frame )
    local args = frame.args
    local plain = toBoolean( frame.args.plain, false );
    frame.args.nocat = toBoolean( frame.args.nocat, false );
    frame.args.references = toBoolean( frame.args.references, true );

    -- проверка на отсутствие обязательного параметра property 
    if not frame.args.property then
        throwError( 'property-param-not-provided' )
    end

-- если значение передано в параметрах вызова то выводим только его
    if frame.args.value and frame.args.value ~= '' then
        if plain or frame.args.nocat or frame:callParserFunction( '#property', frame.args.property )=='' then
                -- опция, запрещающая оформление значения, поэтому никак не трогаем
                return frame.args.value
        else
                -- если трогать всё-таки можно, добавляем категорию-маркер
                return args.value .. categoryLocalValuePresent;
        end
    end

    if ( plain ) then -- вызова стандартного обработчика без оформления, если передана опция plain
    	return frame:callParserFunction( '#property', frame.args.property );
    end

	g_frame = frame
	-- после проверки всех аргументов -- вызов функции оформления для свойства (набора утверждений)
    return formatProperty( frame.args )
end

--[[
  Функция оформления ссылок на источники (reference) 
  Подробнее о ссылках на источники см. d:Wikidata:Glossary/ru

  Экспортируется в качестве зарезервированной точки для вызова из функций-расширения вида claim-module/claim-function через context
  Вызов из других модулей напрямую осуществляться не должен (используйте frame:expandTemplate вместе с одним из специлизированных шаблонов вывода значения свойства).

  Принимает: объект-таблицу утверждение
  Возвращает: строку оформленных ссылок для отображения в статье
]]
function formatRefs( context, options, statement )
	if ( not context ) then error( 'context not specified' ); end;
	if ( not options ) then error( 'options not specified' ); end;
	if ( not options.entity ) then error( 'options.entity missing' ); end;
	if ( not statement ) then error( 'statement not specified' ); end;

	if ( not outputReferences ) then
		return '';
	end

	local result = '';
	if ( statement.references ) then

		local allReferences = statement.references;
		local hasPreferred = false;
		for _, reference in pairs( statement.references ) do
			if ( reference.snaks
					and reference.snaks.P248
					and reference.snaks.P248[1]
					and reference.snaks.P248[1].datavalue
					and reference.snaks.P248[1].datavalue.value["numeric-id"] ) then
				local entityId = "Q" .. reference.snaks.P248[1].datavalue.value["numeric-id"];
				if ( preferredSources[entityId] ) then
					hasPreferred = true;
				end
			end
		end

		for _, reference in pairs( statement.references ) do
			local display = true;
			if ( hasPreferred ) then
				if ( reference.snaks
						and reference.snaks.P248
						and reference.snaks.P248[1]
						and reference.snaks.P248[1].datavalue
						and reference.snaks.P248[1].datavalue.value["numeric-id"] ) then
					local entityId = "Q" .. reference.snaks.P248[1].datavalue.value["numeric-id"];
					if ( deprecatedSources[entityId] ) then
						display = false;
					end
				end
			end
			if ( display ) then
				result = result .. moduleSources.renderReference( g_frame, options.entity, reference );
			end
		end
	end
	return result
end

return p

--[[ Служебные функции для конверсии викитекста
Синтаксис: функция со строчной буквы - для {{#invoke:wikitext|fun|...}}, с заглавной - для require(wikitext).Fun() ]]
specns={file='file',image='file',category='cat', ['к']='cat',['категори']='cat',['файл']='file',['ӳкерчĕк']='file'}
linkprefs="http:// gopher:// https:// mailto: news:// ftp:// irc:// //"
local function Delink(text,repl,cat,file,href,colon) return ( --удаление вики-ссылок mw.ustring.gsub(text, "%[%[(%s*([^|%]]+)%s*)|?(.-)%]%](%a*)", function(a,l,c,e) l = l:gsub("%s+"," "); if specns[mw.ustring.lower(l):match("^(.*):")] then -- ссылка на специальное п. и. if specns[mw.ustring.lower(l):match("^(.*):")]=='cat' then--категория if cat then -- строчка замены, %1=catname, %2=key, %3=mode, %%="%" return table.concat{ l:gsub("^.-:%s*",''), '|', c }:gsub( "^(.-)|([^|]*)|?(.-)$", cat ) .. e else return e -- удалять по умолчанию все категории end else--файл. Fixme:пока все поля описания файла не выдаются шаблонам if file then -- строка замены, %1=filename, %2=parameters, %%="%" return table.concat{ l:gsub("^.-:%s*",''), '|', c }:gsub( "^(.-)|(.*)", file ) .. e else return e -- вырезать фалы end end--if(файл или категория) end--if(спец. пространства) l=string.gsub(l,"^: *", colon or "%1");-- замена начального ":" if repl then return mw.ustring.gsub(l..'|'..(c=='' and a..e or c..e),"^(.-)|(.*)$",repl) else if c=='' then return a..e else return c..e end-- вырезаем end end ):gsub( "(%[([%a%._\/~%%%-%+&#%?!=%(%)@]+)%s*(.-)%])", function(a,r,t) --вырезатор внешних ссылок local p,i=r:match('^(%a*:?//)(.*)') if not p then p,i=r:match('^(mailto:)(.*)') end if p then-- Это URL if href then return (p..' '..i..' '..t):gsub("^(.-) (.-) (.*)$",href) else return t end else-- Это не URL return a end end--вырезатор ) ) end
local function Detag(t) if t == nil or t == '' then return '' end -- Вырезаем кавычки и > из литералов в тэгах, а потом сами литералы local text,j,i,k=t,0; repeat text,i=text:gsub('(</?%a[^>"\']*")[^"]*\\["\\]',"%1");--первая \", \\ из 1-го литерала text,k=text:gsub("(</?%a[^>\"']*')[^']*\\['\\]","%1");--первая \', \\ из 1-го литерала if i+k==0 then--1-й литерал очищен text,j=text:gsub("(</?%a[^>]*)(['\"]).-%2","%1") end until i+j+k==0 -- и вырезать тэги, а также ''курсив'' и '''жирный''' return (text:gsub("'''?",""):gsub('</?%a[^>]*>','')) end
local function Nodis(t) -- Убирает уточнения в скобках return (t:gsub('%s*%([^%)]+%)$','')) end
return { delink=function(f) -- Удаляет ссылки из викитекста local tf=f:getParent() return Delink( f.args[1] or tf.args[1], f.args['repl'] or tf.args['repl'], f.args['cat'] or tf.args['cat'], f.args['file'] or tf.args['file'], f.args['href'] or tf.args['href'], f.args[':'] or tf.args[':'] ) end; Delink=Delink;
detag=function(f) return Detag(f.args[1] or f:getParent().args[1]) end; Detag=Detag;
plain=function(f) local tf=f:getParent() return Detag(Delink( f.args[1] or tf.args[1], f.args['repl'] or tf.args['repl'], f.args['cat'] or tf.args['cat'], f.args['file'] or tf.args['file'], f.args['href'] or tf.args['href'], f.args[':'] or tf.args[':'] )); end;
nodis=function(f) return Nodis(f.args[1] or f:getParent().args[1]) end;Nodis=Nodis }

